import React, { useEffect, useRef, useState } from 'react';
import './Home.css'; // Ensure your CSS is included

const ParticleText = () => {
  const canvasRef = useRef(null);
  const [hover, setHover] = useState(false);
  const [userCoords, setUserCoords] = useState({ x: 0, y: 0 });
  const [centerCoords, setCenterCoords] = useState({ x: window.innerWidth / 2, y: window.innerHeight / 2 });

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const bufferCanvas = document.createElement('canvas');
    const bufferCtx = bufferCanvas.getContext('2d');
    let particles = [];

    const options = {
      mouse: {
        lerpAmt: 0.5,
        repelThreshold: 100,
      },
      particles: {
        density: 3,
        get pixelDensity() {
          return (4 - this.density) * 4;
        },
        pLerpAmt: 0.25,
        vLerpAmt: 0.1,
      },
      text: {
        fontColor: [60, 200, 255, 255],
        fontSize: 120,
        message: 'PARTICLE TEXT',
        get fontStyle() {
          return `${this.fontSize}px Oswald, sans-serif`;
        },
      },
    };

    const particleProps = ['x', 'y', 'vx', 'vy', 'bx', 'by'];

    const setup = () => {
      // Adjust canvas size on setup
      bufferCanvas.width = canvas.width = window.innerWidth;
      bufferCanvas.height = canvas.height = window.innerHeight;

      // Other setup tasks...
      setCenterCoords({ x: canvas.width / 2, y: canvas.height / 2 });
      // Add particle setup logic...
    };

    const run = () => {
      requestAnimationFrame(run);
      update();
      render();
    };

    const update = () => {
      const repelX = hover ? userCoords.x : centerCoords.x;
      const repelY = hover ? userCoords.y : centerCoords.y;

      // Logic for updating particles...
    };

    const render = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      // Render particles and frame...
    };

    const handleMouseMove = (event) => {
      setHover(event.type === 'mousemove');
      setUserCoords({ x: event.clientX, y: event.clientY });
    };

    const handleResize = () => {
      setup();
    };

    // Set up event listeners
    window.addEventListener('mousemove', handleMouseMove);
    window.addEventListener('resize', handleResize);

    // Initial setup and run loop
    setup();
    run();

    return () => {
      // Cleanup event listeners on unmount
      window.removeEventListener('mousemove', handleMouseMove);
      window.removeEventListener('resize', handleResize);
    };
  }, [hover, userCoords, centerCoords]);

  return <canvas ref={canvasRef} />;
};

export default ParticleText;
