import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';
import { FontLoader } from 'three/examples/jsm/loaders/FontLoader.js';
import { TextGeometry } from 'three/examples/jsm/geometries/TextGeometry.js';
import Navbar from './components/Navbar';
import './App.css';

function App() {
  const mountRef = useRef(null);

  useEffect(() => {
    // Vertex Shader
    const vertexShader = `
      uniform vec3 lightPosition;
      varying float vDist;

      void main() {
          vec3 transformedPosition = (modelViewMatrix * vec4(position, 1.0)).xyz;

          // Calculate the distance from the light source to the vertex
          float dist = distance(lightPosition, transformedPosition);
          vDist = dist;

          // Apply a warp based on distance
          vec3 direction = normalize(lightPosition - transformedPosition);
          vec3 offset = direction * (1.0 / (dist + 0.1)) * 1.2; // Warp effect
          transformedPosition += offset;

          gl_Position = projectionMatrix * vec4(transformedPosition, 1.0);
      }
    `;

    // Fragment Shader for the Text
    const fragmentShaderText = `
      varying float vDist;
      uniform vec3 lightColor; // Uniform to receive light color

      void main() {
          // Calculate intensity based on distance
          float intensity = 1.0 / (vDist * vDist + 0.1);
          intensity = intensity * 40.0;
          intensity = clamp(intensity, 0.0, 1.0);

          // Apply the light color and intensity to the output color
          vec3 color = lightColor * intensity; // Multiply the light color by the intensity

          gl_FragColor = vec4(color, 1.0); // Use the calculated color for the fragment
      }
    `;

    // Scene, Camera, Renderer
    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(
      75,
      window.innerWidth / window.innerHeight,
      0.1,
      1000
    );
    const renderer = new THREE.WebGLRenderer({ antialias: true });

    renderer.setSize(window.innerWidth, window.innerHeight);
    mountRef.current.appendChild(renderer.domElement);

    // Add a point light to act as the dynamic light source
    const pointLight = new THREE.PointLight(0xffffff, 2, 100); // White light with increased intensity
    pointLight.decay = 1; // Adjust decay for desired falloff
    pointLight.castShadow = true;
    scene.add(pointLight);

    // Uniforms for the shader
    const uniforms = {
      lightPosition: { value: new THREE.Vector3(0, 0, 0) },
      lightColor: { value: new THREE.Color(0xffffff) } // Set light color for the text
    };

    // Load the font and create the text
    const loader = new FontLoader();
    loader.load(
      'https://threejs.org/examples/fonts/helvetiker_regular.typeface.json',
      function (font) {
        const textGeometry = new TextGeometry(
          'HCI Explorer of digital-physical experiences',
          {
            font: font,
            size: 0.2,
            height: 0.0,
            curveSegments: 12,
            bevelEnabled: false,
          }
        );

        textGeometry.computeBoundingBox();
        const textWidth =
          textGeometry.boundingBox.max.x - textGeometry.boundingBox.min.x;
        const textHeight =
          textGeometry.boundingBox.max.y - textGeometry.boundingBox.min.y;
        const xPosition = -textWidth / 2;
        const yPosition = 2.5; // Keep the original vertical position of the text

        // Create the group for text and background
        const textGroup = new THREE.Group();

        // Create the background rectangle
        const backgroundGeometry = new THREE.PlaneGeometry(
          textWidth + 0.1, // Slightly larger than the text
          textHeight + 0.1
        );
        const backgroundMaterial = new THREE.MeshBasicMaterial({
          color: 0x0000ff, // Blue color for the rectangle
          side: THREE.DoubleSide, // Render both sides
        });
        const backgroundMesh = new THREE.Mesh(backgroundGeometry, backgroundMaterial);
        backgroundMesh.position.set(0, 0, -0.01); // Center the rectangle behind the text

        // Create the text mesh
        const textMaterial = new THREE.ShaderMaterial({
          vertexShader: vertexShader,
          fragmentShader: fragmentShaderText,
          uniforms: uniforms,
        });

        const textMesh = new THREE.Mesh(textGeometry, textMaterial);
        textMesh.position.set(0, 0, 0); // Positioned at origin of the group

        // Add both the text and background to the group
        textGroup.add(backgroundMesh);
        textGroup.add(textMesh);
        textGroup.position.set(xPosition, yPosition, 0); // Center the group in the scene

        // Add the group to the scene
        scene.add(textGroup);
      }
    );

    // Camera Position
    camera.position.z = 5;

    // Handle window resize
    const handleResize = () => {
      const width = window.innerWidth;
      const height = window.innerHeight;
      renderer.setSize(width, height);
      camera.aspect = width / height;
      camera.updateProjectionMatrix();
    };
    window.addEventListener('resize', handleResize);

    // Animate light following the mouse
    let mouseX = 0;
    let mouseY = 0;

    const onMouseMove = (event) => {
      mouseX = (event.clientX / window.innerWidth) * 2 - 1;
      mouseY = -(event.clientY / window.innerHeight) * 2 + 1;
    };

    document.addEventListener('mousemove', onMouseMove);

    function animate() {
      requestAnimationFrame(animate);

      // Convert the mouse position to 3D coordinates
      const vector = new THREE.Vector3(mouseX, mouseY, 0.5);
      vector.unproject(camera);
      const dir = vector.sub(camera.position).normalize();
      const distance = -camera.position.z / dir.z;

      // Position the light slightly in front of the text
      const pos = camera.position.clone().add(dir.multiplyScalar(distance));
      pos.z += 1; // Keep the light in front of the text
      pointLight.position.copy(pos);

      // Update the light position uniform for the shader
      uniforms.lightPosition.value.copy(pos);

      renderer.render(scene, camera);
    }

    animate();

    return () => {
      // Clean up event listeners and Three.js resources on component unmount
      document.removeEventListener('mousemove', onMouseMove);
      window.removeEventListener('resize', handleResize);
      mountRef.current.removeChild(renderer.domElement);
      renderer.dispose();
    };
  }, []);

  return (
    <div>
      <Navbar />
      <div ref={mountRef} style={{ width: '100vw', height: '100vh' }} />
    </div>
  );
}

export default App;
