import React, { useEffect, useRef } from 'react';
import * as d3 from 'd3';
import './About.css';  // Import custom styles

function About() {
    const svgRef = useRef(null);

    useEffect(() => {
        const svg = d3.select(svgRef.current),
            width = window.innerWidth / 2, // Adjust width for the graph (half of the screen)
            height = window.innerHeight;

        svg.attr("viewBox", `0 0 ${width} ${height}`)
            .attr("preserveAspectRatio", "xMidYMid meet");

        const nodes = [
            { id: 1, label: "Studied at the OCAD University" },
            { id: 2, label: "Worked as a Conversation and UX Designer at RBC" },
            { id: 3, label: "Worked as a UX Designer at Nokia" },
            { id: 4, label: "Studied abroad at the Hong Kong Polytechnic University" },
            { id: 5, label: "Working as an Interaction Designer/Researcher at Huawei R&D labs" },
            { id: 6, label: "Worked as a Designer at Jack/Cover Health" },
            { id: 7, label: "Collaborated with cloth vendors to redesign the Pang Jai Fabric Market" },
            { id: 8, label: "Read Speculative Design" },
            { id: 9, label: "Category winner at several hackathons" },
            { id: 14, label: "Won the 2021 ADA mentorship program" },
            { id: 10, label: "Started HCI coding projects" },
            { id: 11, label: "Co-founded the UX Student Association" },
            { id: 12, label: "Founded the OCADU Undergraduate Research Association" },
            { id: 13, label: "Read Design Justice and 99% Invisible Cities" },
            { id: 15, label: "Human-centred design" },
            { id: 16, label: "Research" },
            { id: 17, label: "Co-Design" },
            { id: 18, label: "Speculative Innovation" }
        ];

        const links = [
            { source: 1, target: 2 },
            { source: 2, target: 3 },
            { source: 3, target: 4 },
            { source: 4, target: 5 },
            { source: 1, target: 6 },
            { source: 4, target: 7 },
            { source: 1, target: 8 },
            { source: 1, target: 9 },
            { source: 1, target: 10 },
            { source: 1, target: 11 },
            { source: 1, target: 12 },
            { source: 7, target: 13 },
            { source: 9, target: 14 },
            { source: 1, target: 9 },
            { source: 15, target: 1 },
            { source: 15, target: 2 },
            { source: 15, target: 3 },
            { source: 15, target: 5 },
            { source: 15, target: 7 },
            { source: 15, target: 11 },
            { source: 16, target: 5 },
            { source: 16, target: 2 },
            { source: 16, target: 12 },
            { source: 17, target: 13 },
            { source: 17, target: 7 },
            { source: 18, target: 8 },
            { source: 18, target: 5 },
            { source: 18, target: 10 }

        ];

        // Main force simulation for nodes and links
        const simulation = d3.forceSimulation(nodes)
            .force("link", d3.forceLink(links).id(d => d.id).distance(150))  // Spread out linked nodes
            .force("charge", d3.forceManyBody().strength(-100))  // Strong repulsion to scatter nodes
            .force("center", d3.forceCenter(width / 2, height / 2))
            .force("collision", d3.forceCollide().radius(90))  // Avoid node overlap
            .on("tick", ticked);

        // Create link elements
        const link = svg.append("g")
            .attr("class", "links")
            .selectAll("line")
            .data(links)
            .enter().append("line")
            .attr("class", "link");

        // Create node elements
        const node = svg.append("g")
            .attr("class", "nodes")
            .selectAll("circle")
            .data(nodes)
            .enter().append("circle")
            .attr("class", "node")
            .attr("r", 5)
            .call(d3.drag()
                .on("start", dragStarted)
                .on("drag", dragged)
                .on("end", dragEnded))
            .on("mouseover", function (event, d) {
                simulation.alphaTarget(0.3).restart();
                d.fx = d.x - (Math.random() * 10 - 5);  // Slight random move on hover
                d.fy = d.y - (Math.random() * 10 - 5);
            })
            .on("mouseout", function (event, d) {
                simulation.alphaTarget(0);
                d.fx = null;
                d.fy = null;
            });

        // Create text labels
        const label = svg.append("g")
            .attr("class", "labels")
            .selectAll("text")
            .data(nodes)
            .enter().append("text")
            .attr("class", d => [15, 16, 17, 18].includes(d.id) ? "label header-label" : "label")
            .attr("dx", 12)
            .attr("dy", ".35em")
            .text(d => d.label);

        // Separate simulation for labels to prevent overlap
        const labelForce = d3.forceSimulation(nodes)
            .force("labelCollision", d3.forceCollide().radius(20))  // Prevent label overlap
            .on("tick", () => {
                label
                    .attr("x", d => d.x)
                    .attr("y", d => d.y);
            });

        // Function to update positions on each tick of the simulation
        function ticked() {
            link
                .attr("x1", d => d.source.x)
                .attr("y1", d => d.source.y)
                .attr("x2", d => d.target.x)
                .attr("y2", d => d.target.y);

            node
                .attr("cx", d => d.x)
                .attr("cy", d => d.y);

            label
                .attr("x", d => d.x)
                .attr("y", d => d.y);
        }

        // Drag functions to allow nodes to be dragged
        function dragStarted(event, d) {
            if (!event.active) simulation.alphaTarget(0.3).restart();
            d.fx = d.x;
            d.fy = d.y;
        }

        function dragged(event, d) {
            d.fx = event.x;
            d.fy = event.y;
        }

        function dragEnded(event, d) {
            if (!event.active) simulation.alphaTarget(0);
            d.fx = null;
            d.fy = null;
        }

    }, []);

    return (
        <div className="about-container" style={{ backgroundColor: 'black', minHeight: '100vh', display: 'flex', flexDirection: 'row' }}>
            <div className="about-text" style={{ width: '50%', color: 'white', padding: '20px' }}>
                <h1>About Me</h1>
                <p>
                    I am a passionate Interaction Designer with a strong background in Human-Computer Interaction (HCI).
                    My experience spans across various industries, including tech giants like Nokia and RBC,
                    and cutting-edge research at Huawei R&D labs.
                </p>
                <p>
                    My journey in design started at OCAD University, where I delved deep into the world of speculative design
                    and human-centred innovation. I have also co-founded the UX Student Association and the OCADU Undergraduate
                    Research Association, where I mentored and collaborated with budding designers and researchers.
                </p>
                <p>
                    My work is driven by a commitment to co-design, research, and speculative innovation, aiming to push the
                    boundaries of how technology can be ethically integrated into everyday life.
                </p>
            </div>
            <div className="about-graph" style={{ width: '50%' }}>
                <svg ref={svgRef} style={{ width: '100%', height: '100%' }}></svg>
            </div>
        </div>
    );
}

export default About;
