import React, { useEffect, useRef, useState } from 'react';
import './App.css';

const ParticleText = () => {
  const canvasRef = useRef(null);
  const [hover, setHover] = useState(false);
  const [userCoords, setUserCoords] = useState({ x: 0, y: 0 });
  const [centerCoords, setCenterCoords] = useState({
    x: window.innerWidth / 2,
    y: window.innerHeight / 2,
  });

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    // Create an offscreen canvas buffer
    const bufferCanvas = document.createElement('canvas');
    const bufferCtx = bufferCanvas.getContext('2d');

    const options = {
      mouse: {
        lerpAmt: 0.5,
        repelThreshold: 100,
      },
      particles: {
        density: 3,
        get pixelDensity() {
          return (4 - this.density) * 4;
        },
        pLerpAmt: 0.25,
        vLerpAmt: 0.1,
      },
      text: {
        fontColor: [60, 200, 255, 255],
        fontSize: 120,
        message: 'PARTICLE TEXT',
        get fontStyle() {
          return `${this.fontSize}px Oswald, sans-serif`;
        },
      },
    };

    const setup = () => {
      // Set canvas size
      bufferCanvas.width = canvas.width = window.innerWidth;
      bufferCanvas.height = canvas.height = window.innerHeight;

      // Set center coordinates based on window size
      setCenterCoords({
        x: window.innerWidth / 2,
        y: window.innerHeight / 2,
      });

      // Add other setup logic here...
    };

    const handleResize = () => {
      setup(); // Resize canvas and update center coordinates
    };

    const run = () => {
      requestAnimationFrame(run);
      // Add your update and render logic here...
    };

    const handleMouseMove = (event) => {
      setHover(true);
      setUserCoords({ x: event.clientX, y: event.clientY });
    };

    const handleMouseOut = () => {
      setHover(false);
    };

    // Set up event listeners
    window.addEventListener('mousemove', handleMouseMove);
    window.addEventListener('mouseout', handleMouseOut);
    window.addEventListener('resize', handleResize);

    // Initial setup and run loop
    setup();
    run();

    return () => {
      // Cleanup event listeners on unmount
      window.removeEventListener('mousemove', handleMouseMove);
      window.removeEventListener('mouseout', handleMouseOut);
      window.removeEventListener('resize', handleResize);
    };
  }, []); // Empty dependency array ensures useEffect runs only once on mount

  return <canvas ref={canvasRef} />;
};

export default ParticleText;
