import React, { useEffect } from 'react';
import * as THREE from 'three';
import { FontLoader } from 'three/examples/jsm/loaders/FontLoader.js';
import { Container, Row, Col } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import './Home.css'; // Ensure you have this CSS file for custom styles
import image1 from '../assets/img1.png';
import image2 from '../assets/img2.png';
import image3 from '../assets/img3.png';
import image4 from '../assets/img4.png';
import image5 from '../assets/img5.png';
import image6 from '../assets/img6.png';

const Home = () => {

    // Three.js and particle text effect logic
    useEffect(() => {
        const preload = () => {
            let manager = new THREE.LoadingManager();
            manager.onLoad = function () {
                const environment = new Environment(typo, particle);
            };

            let typo = null;
            const loader = new FontLoader(manager);
            loader.load(
                'https://res.cloudinary.com/dydre7amr/raw/upload/v1612950355/font_zsd4dr.json',
                function (font) {
                    typo = font;
                }
            );
            const particle = new THREE.TextureLoader(manager).load(
                'https://res.cloudinary.com/dfvtkoboz/image/upload/v1605013866/particle_a64uzf.png'
            );
        };

        if (
            document.readyState === 'complete' ||
            (document.readyState !== 'loading' && !document.documentElement.doScroll)
        )
            preload();
        else document.addEventListener('DOMContentLoaded', preload);

        class Environment {
            constructor(font, particle) {
                this.font = font;
                this.particle = particle;
                this.container = document.querySelector('#magic');
                this.scene = new THREE.Scene();
                this.createCamera();
                this.createRenderer();
                this.setup();
                this.bindEvents();
            }

            bindEvents() {
                window.addEventListener('resize', this.onWindowResize.bind(this));
            }

            setup() {
                // Make sure to store the created particles instance in a variable for reference
                this.createParticles = new CreateParticles(
                    this.scene,
                    this.font,
                    this.particle,
                    this.camera,
                    this.renderer
                );
            }

            // Define the render method and ensure it is referencing the correct objects
            render() {
                if (this.createParticles) {
                    this.createParticles.render();  // Ensure that `createParticles` is initialized
                }
                this.renderer.render(this.scene, this.camera);  // Render the scene and camera
            }

            createCamera() {
                this.camera = new THREE.PerspectiveCamera(
                    65,
                    this.container.clientWidth / this.container.clientHeight,
                    1,
                    10000
                );
                this.camera.position.set(0, 0, 100);
            }

            createRenderer() {
                this.renderer = new THREE.WebGLRenderer();
                this.renderer.setSize(this.container.clientWidth, this.container.clientHeight);
                this.renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));
                this.container.appendChild(this.renderer.domElement);
                this.renderer.setAnimationLoop(() => {
                    this.render();  // Ensure the render method is correctly called
                });
            }

            onWindowResize() {
                this.camera.aspect = this.container.clientWidth / this.container.clientHeight;
                this.camera.updateProjectionMatrix();
                this.renderer.setSize(this.container.clientWidth, this.container.clientHeight);
            }
        }


        class CreateParticles {
            constructor(scene, font, particleImg, camera, renderer) {
                this.scene = scene;
                this.font = font;
                this.particleImg = particleImg;
                this.camera = camera;
                this.renderer = renderer;

                this.raycaster = new THREE.Raycaster();
                this.mouse = new THREE.Vector2(-200, 200);

                this.colorChange = new THREE.Color();
                this.buttom = false;

                this.data = {
                    text: 'FUTURE\nIS NOW',
                    amount: 1500,
                    particleSize: 1,
                    particleColor: 0xffffff,
                    textSize: 16,
                    area: 250,
                    ease: 0.05,
                };

                this.setup();
                this.bindEvents();
            }

            setup() {
                const geometry = new THREE.PlaneGeometry(
                    this.visibleWidthAtZDepth(100, this.camera),
                    this.visibleHeightAtZDepth(100, this.camera)
                );
                const material = new THREE.MeshBasicMaterial({
                    color: 0x00ff00,
                    transparent: true,
                });
                this.planeArea = new THREE.Mesh(geometry, material);
                this.planeArea.visible = false;
                this.createText();
            }

            visibleWidthAtZDepth(depth, camera) {
                const cameraOffset = camera.position.z;
                if (depth < cameraOffset) depth -= cameraOffset;
                else depth += cameraOffset;

                const vFOV = (camera.fov * Math.PI) / 180;
                return 2 * Math.tan(vFOV / 2) * Math.abs(depth) * camera.aspect;
            }

            visibleHeightAtZDepth(depth, camera) {
                const cameraOffset = camera.position.z;
                if (depth < cameraOffset) depth -= cameraOffset;
                else depth += cameraOffset;

                const vFOV = (camera.fov * Math.PI) / 180;
                return 2 * Math.tan(vFOV / 2) * Math.abs(depth);
            }

            bindEvents() {
                document.addEventListener('mousedown', (event) => this.onMouseDown(event));
                document.addEventListener('mousemove', (event) => this.onMouseMove(event));
                document.addEventListener('mouseup', (event) => this.onMouseUp(event));
            }

            onMouseDown(event) {
                this.mouse.x = (event.clientX / window.innerWidth) * 2 - 1;
                this.mouse.y = -(event.clientY / window.innerHeight) * 2 + 1;

                const vector = new THREE.Vector3(this.mouse.x, this.mouse.y, 0.5);
                vector.unproject(this.camera);
                const dir = vector.sub(this.camera.position).normalize();
                const distance = -this.camera.position.z / dir.z;
                this.currenPosition = this.camera.position
                    .clone()
                    .add(dir.multiplyScalar(distance));

                const pos = this.particles.geometry.attributes.position;
                this.buttom = true;
                this.data.ease = 0.01;
            }

            onMouseUp() {
                this.buttom = false;
                this.data.ease = 0.05;
            }

            onMouseMove(event) {
                this.mouse.x = (event.clientX / window.innerWidth) * 2 - 1;
                this.mouse.y = -(event.clientY / window.innerHeight) * 2 + 1;
            }

            createText() {
                let thePoints = [];
                let shapes = this.font.generateShapes(this.data.text, this.data.textSize);
                let geometry = new THREE.ShapeGeometry(shapes);
                geometry.computeBoundingBox();

                const xMid =
                    -0.5 * (geometry.boundingBox.max.x - geometry.boundingBox.min.x);
                const yMid = (geometry.boundingBox.max.y - geometry.boundingBox.min.y) / 2.85;

                geometry.center();

                let holeShapes = [];

                for (let q = 0; q < shapes.length; q++) {
                    let shape = shapes[q];

                    if (shape.holes && shape.holes.length > 0) {
                        for (let j = 0; j < shape.holes.length; j++) {
                            let hole = shape.holes[j];
                            holeShapes.push(hole);
                        }
                    }
                }

                shapes.push.apply(shapes, holeShapes);

                let colors = [];
                let sizes = [];

                for (let x = 0; x < shapes.length; x++) {
                    let shape = shapes[x];

                    const amountPoints = shape.type === 'Path' ? this.data.amount / 2 : this.data.amount;

                    let points = shape.getSpacedPoints(amountPoints);

                    points.forEach((element) => {
                        const a = new THREE.Vector3(element.x, element.y, 0);
                        thePoints.push(a);
                        colors.push(this.colorChange.r, this.colorChange.g, this.colorChange.b);
                        sizes.push(1);
                    });
                }

                let geoParticles = new THREE.BufferGeometry().setFromPoints(thePoints);
                geoParticles.translate(xMid, yMid, 0);

                geoParticles.setAttribute(
                    'customColor',
                    new THREE.Float32BufferAttribute(colors, 3)
                );
                geoParticles.setAttribute('size', new THREE.Float32BufferAttribute(sizes, 1));

                const material = new THREE.ShaderMaterial({
                    uniforms: {
                        color: { value: new THREE.Color(0xffffff) },
                        pointTexture: { value: this.particleImg },
                    },
                    vertexShader: document.getElementById('vertexshader').textContent,
                    fragmentShader: document.getElementById('fragmentshader').textContent,
                    blending: THREE.AdditiveBlending,
                    depthTest: false,
                    transparent: true,
                });

                this.particles = new THREE.Points(geoParticles, material);
                this.scene.add(this.particles);

                this.geometryCopy = new THREE.BufferGeometry();
                this.geometryCopy.copy(this.particles.geometry);
            }

            render() {
                // Raycasting and particle update logic goes here
                const time = ((0.001 * performance.now()) % 12) / 12;
                const zigzagTime = (1 + Math.sin(time * 2 * Math.PI)) / 6;

                this.raycaster.setFromCamera(this.mouse, this.camera);

                const intersects = this.raycaster.intersectObject(this.planeArea);

                if (intersects.length > 0) {
                    const pos = this.particles.geometry.attributes.position;
                    const copy = this.geometryCopy.attributes.position;
                    const colors = this.particles.geometry.attributes.customColor;
                    const size = this.particles.geometry.attributes.size;

                    const mx = intersects[0].point.x;
                    const my = intersects[0].point.y;
                    const mz = intersects[0].point.z;

                    for (let i = 0; i < pos.count; i++) {
                        const initX = copy.getX(i);
                        const initY = copy.getY(i);
                        const initZ = copy.getZ(i);

                        let px = pos.getX(i);
                        let py = pos.getY(i);
                        let pz = pos.getZ(i);

                        this.colorChange.setHSL(0.5, 1, 1);
                        colors.setXYZ(i, this.colorChange.r, this.colorChange.g, this.colorChange.b);
                        colors.needsUpdate = true;

                        size.array[i] = this.data.particleSize;
                        size.needsUpdate = true;

                        let dx = mx - px;
                        let dy = my - py;
                        const dz = mz - pz;

                        const mouseDistance = this.distance(mx, my, px, py);
                        let d = (dx = mx - px) * dx + (dy = my - py) * dy;
                        const f = -this.data.area / d;

                        if (this.buttom) {
                            const t = Math.atan2(dy, dx);
                            px -= f * Math.cos(t);
                            py -= f * Math.sin(t);

                            this.colorChange.setHSL(0.5 + zigzagTime, 1.0, 0.5);
                            colors.setXYZ(i, this.colorChange.r, this.colorChange.g, this.colorChange.b);
                            colors.needsUpdate = true;

                            if (
                                px > initX + 70 ||
                                px < initX - 70 ||
                                py > initY + 70 ||
                                py < initY - 70
                            ) {
                                this.colorChange.setHSL(0.15, 1.0, 0.5);
                                colors.setXYZ(i, this.colorChange.r, this.colorChange.g, this.colorChange.b);
                                colors.needsUpdate = true;
                            }
                        } else {
                            if (mouseDistance < this.data.area) {
                                if (i % 5 === 0) {
                                    const t = Math.atan2(dy, dx);
                                    px -= 0.03 * Math.cos(t);
                                    py -= 0.03 * Math.sin(t);

                                    this.colorChange.setHSL(0.15, 1.0, 0.5);
                                    colors.setXYZ(i, this.colorChange.r, this.colorChange.g, this.colorChange.b);
                                    colors.needsUpdate = true;

                                    size.array[i] = this.data.particleSize / 1.2;
                                    size.needsUpdate = true;
                                } else {
                                    const t = Math.atan2(dy, dx);
                                    px += f * Math.cos(t);
                                    py += f * Math.sin(t);

                                    pos.setXYZ(i, px, py, pz);
                                    pos.needsUpdate = true;

                                    size.array[i] = this.data.particleSize * 1.3;
                                    size.needsUpdate = true;
                                }

                                if (
                                    px > initX + 10 ||
                                    px < initX - 10 ||
                                    py > initY + 10 ||
                                    py < initY - 10
                                ) {
                                    this.colorChange.setHSL(0.15, 1.0, 0.5);
                                    colors.setXYZ(i, this.colorChange.r, this.colorChange.g, this.colorChange.b);
                                    colors.needsUpdate = true;

                                    size.array[i] = this.data.particleSize / 1.8;
                                    size.needsUpdate = true;
                                }
                            }
                        }

                        px += (initX - px) * this.data.ease;
                        py += (initY - py) * this.data.ease;
                        pz += (initZ - pz) * this.data.ease;

                        pos.setXYZ(i, px, py, pz);
                        pos.needsUpdate = true;
                    }
                }
            }

            distance(x1, y1, x2, y2) {
                return Math.sqrt(Math.pow(x1 - x2, 2) + Math.pow(y1 - y2, 2));
            }
        }

        // Vertex and Fragment shader scripts
        const vertexShaderScript = document.createElement('script');
        vertexShaderScript.type = 'x-shader/x-vertex';
        vertexShaderScript.id = 'vertexshader';
        vertexShaderScript.text = `
      attribute float size;
      attribute vec3 customColor;
      varying vec3 vColor;
      void main() {
        vColor = customColor;
        vec4 mvPosition = modelViewMatrix * vec4(position, 1.0);
        gl_PointSize = size * (300.0 / -mvPosition.z);
        gl_Position = projectionMatrix * mvPosition;
      }
    `;
        document.body.appendChild(vertexShaderScript);

        const fragmentShaderScript = document.createElement('script');
        fragmentShaderScript.type = 'x-shader/x-fragment';
        fragmentShaderScript.id = 'fragmentshader';
        fragmentShaderScript.text = `
      uniform vec3 color;
      uniform sampler2D pointTexture;
      varying vec3 vColor;
      void main() {
        gl_FragColor = vec4(color * vColor, 1.0);
        gl_FragColor = gl_FragColor * texture2D(pointTexture, gl_PointCoord);
      }
    `;
        document.body.appendChild(fragmentShaderScript);

    }, []);

