import React, { useEffect, useRef, useState } from 'react';
import './App.css';

const ParticleText = () => {
  const canvasRef = useRef(null);
  const [mousePos, setMousePos] = useState({ x: 0, y: 0 });

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    // Set canvas size
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;

    // Particle configuration
    const particles = [];
    const numParticles = 100; // Number of particles

    // Create particles
    for (let i = 0; i < numParticles; i++) {
      particles.push({
        x: Math.random() * canvas.width,
        y: Math.random() * canvas.height,
        vx: (Math.random() - 0.5) * 2,
        vy: (Math.random() - 0.5) * 2,
        radius: 3,
      });
    }

    // Mousemove handler
    const handleMouseMove = (e) => {
      setMousePos({ x: e.clientX, y: e.clientY });
    };

    window.addEventListener('mousemove', handleMouseMove);

    // Animation loop
    const render = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height); // Clear canvas

      // Draw each particle
      particles.forEach(particle => {
        const distX = particle.x - mousePos.x;
        const distY = particle.y - mousePos.y;
        const distance = Math.sqrt(distX * distX + distY * distY);

        // Repel particles if they are within a certain distance from the mouse
        if (distance < 100) {
          const forceDirectionX = distX / distance;
          const forceDirectionY = distY / distance;
          const maxDistance = 100;
          const force = (maxDistance - distance) / maxDistance; // The closer the particle, the stronger the force
          const directionX = forceDirectionX * force * 2;
          const directionY = forceDirectionY * force * 2;

          particle.vx += directionX;
          particle.vy += directionY;
        }

        // Update particle position
        particle.x += particle.vx;
        particle.y += particle.vy;

        // Wrap particles around the edges
        if (particle.x > canvas.width) particle.x = 0;
        if (particle.x < 0) particle.x = canvas.width;
        if (particle.y > canvas.height) particle.y = 0;
        if (particle.y < 0) particle.y = canvas.height;

        // Draw the particle
        ctx.beginPath();
        ctx.arc(particle.x, particle.y, particle.radius, 0, Math.PI * 2, false);
        ctx.fillStyle = 'white';
        ctx.fill();
        ctx.closePath();
      });

      requestAnimationFrame(render); // Repeat the render process
    };

    render(); // Start the animation

    return () => {
      // Cleanup
      window.removeEventListener('mousemove', handleMouseMove);
    };
  }, [mousePos]);

  return <canvas ref={canvasRef} />;
};

export default ParticleText;
