import React from 'react';
import './Home.css';
import './Content.css';
import lockscreen from '../assets/Proximity-UI/lockscreen.mov';
import article from '../assets/Proximity-UI/article.mov';

const Home = () => {
    return (
        <div className="container content">
            <div className="row align-items-start">
                <div className="col-md-6 left-column">
                    {/* <div className="fixed-content">



                    </div> */}

                    <div className="scrolling-content">
                        {/* <img src="path-to-eiffel-tower-placeholder.jpg" alt="Eiffel Tower Bottom 2" className="bottom-image" /> */}
                        <video src={article} className="img-fluid w-100" controls loop muted>
                            Your browser does not support the video tag.
                        </video>
                        <video src={lockscreen} className="img-fluid w-100" controls loop muted>
                            Your browser does not support the video tag.
                        </video>
                    </div>
                </div>

                {/* Right Column */}
                <div className="col-md-6 right-column ">
                    <div>
                        <div className="text-content">
                            <h2>Proximity Design</h2>
                            <p>Research, Development(React), UI</p>
                        </div>
                        {/* Add your right column content here */}
                        <p>I like design that is changes, constantly in motion through interaction. There are scenarios where you are near the display, yet not using it. Maybe you are walking around doing activies in the house, scrolling on your phone on your bed, or just trying to get a glimps without walking up close to your device. What are some possiblities of how this space can be used?</p>
                    </div>
                </div>
            </div>
        </div >
    );
};

export default Home;
