function Home() {
    const mountRef = useRef(null);

    useEffect(() => {
        const vertexShader = `
        uniform vec3 lightPosition;
        varying float vDist;

        void main() {
            vec3 transformedPosition = (modelViewMatrix * vec4(position, 1.0)).xyz;

            float dist = distance(lightPosition, transformedPosition);
            vDist = dist;

            gl_Position = projectionMatrix * vec4(transformedPosition, 1.0);
        }
        `;

        const fragmentShader = `
        varying float vDist;
        uniform vec3 lightColor;
        uniform vec2 lightPosition;
        uniform vec2 u_resolution;

        float getLightRayEffect(vec2 uv, vec2 lightPos) {
            vec2 dir = uv - lightPos;
            float dist = length(dir);
            dir /= dist;

            float ray = 1.0 / (dist * dist + 0.1);
            ray = ray * 0.05;
            ray *= sin(dist * 10.0);

            return ray;
        }

        void main() {
            vec2 uv = gl_FragCoord.xy / u_resolution;

            float intensity = 1.0 / (vDist * vDist + 0.1);
            intensity = clamp(intensity, 0.0, 1.0);

            float rays = getLightRayEffect(uv, lightPosition / u_resolution);
            intensity += rays;

            vec3 color = lightColor * intensity;

            gl_FragColor = vec4(color, 1.0);
        }
        `;

        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
        const renderer = new THREE.WebGLRenderer({ antialias: true });

        renderer.setSize(window.innerWidth, window.innerHeight);
        if (mountRef.current) {
            mountRef.current.appendChild(renderer.domElement);
        }

        const pointLight = new THREE.PointLight(0xffffff, 2, 100);
        scene.add(pointLight);

        const uniforms = {
            lightPosition: { value: new THREE.Vector2(0, 0) },
            lightColor: { value: new THREE.Color(0xffffff) },
            u_resolution: { value: new THREE.Vector2(window.innerWidth, window.innerHeight) },
        };

        const loader = new FontLoader();
        loader.load(
            'https://threejs.org/examples/fonts/helvetiker_regular.typeface.json',
            (font) => {
                const textGeometry = new TextGeometry(
                    'Human-computer Interaction Explorer of digital-physical experiences',
                    {
                        font: font,
                        size: 0.2,
                        height: 0.0,
                        curveSegments: 12,
                        bevelEnabled: false,
                    }
                );

                textGeometry.computeBoundingBox();
                const textWidth = textGeometry.boundingBox.max.x - textGeometry.boundingBox.min.x;
                const xPosition = -textWidth / 2;

                const textMaterial = new THREE.ShaderMaterial({
                    vertexShader: vertexShader,
                    fragmentShader: fragmentShader,
                    uniforms: uniforms,
                });

                const textMesh = new THREE.Mesh(textGeometry, textMaterial);
                textMesh.position.set(xPosition, 2.5, 0);
                scene.add(textMesh);
            }
        );

        camera.position.z = 5;

        const handleResize = () => {
            const width = window.innerWidth;
            const height = window.innerHeight;
            renderer.setPixelRatio(window.devicePixelRatio / 2);
            renderer.setSize(width, height);
            camera.aspect = width / height;
            camera.updateProjectionMatrix();

            uniforms.u_resolution.value.set(width, height);
        };
        window.addEventListener('resize', handleResize);

        let mouseX = 0;
        let mouseY = 0;

        const onMouseMove = (event) => {
            mouseX = (event.clientX / window.innerWidth) * 2 - 1;
            mouseY = -(event.clientY / window.innerHeight) * 2 + 1;

            const vector = new THREE.Vector3(mouseX, mouseY, 0.5);
            vector.unproject(camera);
            const dir = vector.sub(camera.position).normalize();
            const distance = -camera.position.z / dir.z;

            const pos = camera.position.clone().add(dir.multiplyScalar(distance));
            pos.z += 1;
            pointLight.position.copy(pos);

            uniforms.lightPosition.value.set(event.clientX, window.innerHeight - event.clientY);
        };

        document.addEventListener('mousemove', onMouseMove);

        function animate() {
            requestAnimationFrame(animate);
            renderer.render(scene, camera);
        }

        animate();

        return () => {
            document.removeEventListener('mousemove', onMouseMove);
            window.removeEventListener('resize', handleResize);
            if (mountRef.current) {
                mountRef.current.removeChild(renderer.domElement);
            }
            renderer.dispose();
        };
    }, []);

    return <div ref={mountRef} className="three-js-container" />;
}

export default Home;
