import React, { useEffect, useRef, useState } from 'react';
import './App.css';

const ParticleText = () => {
  const canvasRef = useRef(null);
  const [mousePos, setMousePos] = useState({ x: -9999, y: -9999 }); // Initially off-screen

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    // Set canvas size
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;

    // Create an offscreen canvas for text rendering
    const bufferCanvas = document.createElement('canvas');
    const bufferCtx = bufferCanvas.getContext('2d');
    bufferCanvas.width = canvas.width;
    bufferCanvas.height = canvas.height;

    const particles = [];
    const numParticles = 5000;

    const text = "HCI Explorer of Digital-Physical Experiences";

    // Step 1: Draw the text on the offscreen buffer canvas
    bufferCtx.font = '50px Arial';
    bufferCtx.fillStyle = 'white';
    bufferCtx.textAlign = 'center';
    bufferCtx.fillText(text, canvas.width / 2, canvas.height / 2);

    // Calculate bounding box for text
    const textWidth = bufferCtx.measureText(text).width;
    const textHeight = 50; // Font size

    // Step 2: Get pixel data of the text from the buffer canvas
    const imageData = bufferCtx.getImageData(0, 0, canvas.width, canvas.height);
    const data = imageData.data;

    // Step 3: Map particles to the text pixels and initialize them near their target
    for (let y = 0; y < canvas.height; y += 4) {
      for (let x = 0; x < canvas.width; x += 4) {
        const index = (y * canvas.width + x) * 4;
        if (data[index + 3] > 150) { // Check if the pixel is opaque
          const randomOffset = 20; // Control how far the particles start from their final position

          particles.push({
            // Start near the target position with a small random offset
            x: x + (Math.random() - 0.5) * randomOffset,
            y: y + (Math.random() - 0.5) * randomOffset,
            bx: x, // Base x position (where the text is)
            by: y, // Base y position (where the text is),
            vx: 0,
            vy: 0,
            radius: 1.5,
            friction: 0.95, // Friction to slow down over time
          });
        }
      }
    }

    // Mousemove handler to update the mouse position
    const handleMouseMove = (e) => {
      setMousePos({ x: e.clientX, y: e.clientY });
    };

    window.addEventListener('mousemove', handleMouseMove);

    // Animation loop
    const render = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height); // Clear canvas

      // Check if the mouse is near the text bounding box
      const isMouseNearText =
        mousePos.x > (canvas.width - textWidth) / 2 - 100 &&
        mousePos.x < (canvas.width + textWidth) / 2 + 100 &&
        mousePos.y > (canvas.height - textHeight) / 2 - 100 &&
        mousePos.y < (canvas.height + textHeight) / 2 + 100;

      particles.forEach((particle) => {
        // Calculate the distance from the base position
        const dx = particle.bx - particle.x;
        const dy = particle.by - particle.y;
        const distToBase = Math.sqrt(dx * dx + dy * dy);

        // If the mouse is near the text, apply a subtle random movement effect
        if (isMouseNearText) {
          particle.vx += (Math.random() - 0.5) * 0.1; // Small horizontal movement
          particle.vy += (Math.random() - 0.5) * 0.1; // Small vertical movement
        }

        // Particles should return to their original positions with friction
        if (distToBase > 2) {
          particle.vx += dx * 0.01; // Small force towards the base
          particle.vy += dy * 0.01; // Small force towards the base
        }

        // Apply friction to slow down movement over time
        particle.vx *= particle.friction;
        particle.vy *= particle.friction;

        // Update the particle's position based on velocity
        particle.x += particle.vx;
        particle.y += particle.vy;

        // Draw the particle on the canvas
        ctx.beginPath();
        ctx.arc(particle.x, particle.y, particle.radius, 0, Math.PI * 2, false);
        ctx.fillStyle = 'white';
        ctx.fill();
        ctx.closePath();
      });

      requestAnimationFrame(render); // Repeat the render process
    };

    render(); // Start the animation

    return () => {
      // Cleanup the mousemove event listener
      window.removeEventListener('mousemove', handleMouseMove);
    };
  }, [mousePos]);

  return <canvas ref={canvasRef} />;
};

export default ParticleText;
