import React, { useEffect, useRef, useState } from 'react';
import './App.css';

const ParticleText = () => {
  const canvasRef = useRef(null);
  const [mousePos, setMousePos] = useState({ x: 0, y: 0 });
  // const [bouncinessDecay, setBouncinessDecay] = useState(true); // Track if bounciness should decay

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    // Set canvas size
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;

    // Create an offscreen canvas for text rendering
    const bufferCanvas = document.createElement('canvas');
    const bufferCtx = bufferCanvas.getContext('2d');
    bufferCanvas.width = canvas.width;
    bufferCanvas.height = canvas.height;

    const particles = [];
    const numParticles = 5000;

    const text = "HCI Explorer of Digital-Physical Experiences";

    // Step 1: Draw the text on the offscreen buffer canvas
    bufferCtx.font = '50px Arial';
    bufferCtx.fillStyle = 'white';
    bufferCtx.textAlign = 'center';
    bufferCtx.fillText(text, canvas.width / 2, canvas.height / 2);

    // Step 2: Get pixel data of the text from the buffer canvas
    const imageData = bufferCtx.getImageData(0, 0, canvas.width, canvas.height);
    const data = imageData.data;

    // Step 3: Map particles to the text pixels and initialize them near their target
    for (let y = 0; y < canvas.height; y += 4) {
      for (let x = 0; x < canvas.width; x += 4) {
        const index = (y * canvas.width + x) * 4;
        if (data[index + 3] > 150) { // Check if the pixel is opaque
          const randomOffset = 20; // Control how far the particles start from their final position

          particles.push({
            // Start near the target position with a small random offset
            x: x + (Math.random() - 0.5) * randomOffset,
            y: y + (Math.random() - 0.5) * randomOffset,
            bx: x, // Base x position (where the text is)
            by: y, // Base y position (where the text is)
            vx: 0,
            vy: 0,
            radius: 1.5,
            friction: 0.85, // Friction to reduce velocity over time
            bounciness: 1.5, // Initial bounciness strength
            bouncinessDecayFactor: 0.98, // Decay factor for bounciness
            minBounciness: 0.1, // Minimum bounciness after decay
          });
        }
      }
    }

    // Mousemove handler to update the mouse position
    const handleMouseMove = (e) => {
      setMousePos({ x: e.clientX, y: e.clientY });
    };

    window.addEventListener('mousemove', handleMouseMove);

    // Reduce bounciness over time
    setTimeout(() => {
      setBouncinessDecay(false); // After some time, stop bounciness decay
    }, 5000); // 5 seconds of initial bounciness

    // Animation loop
    const render = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height); // Clear canvas

      particles.forEach(particle => {
        const distX = particle.x - mousePos.x;
        const distY = particle.y - mousePos.y;
        const distance = Math.sqrt(distX * distX + distY * distY);

        // Decay the bounciness over time (initially strong, then weak)
        if (bouncinessDecay) {
          particle.bounciness *= particle.bouncinessDecayFactor; // Decay bounciness
          if (particle.bounciness < particle.minBounciness) {
            particle.bounciness = particle.minBounciness; // Limit to minimum bounciness
          }
        } else {
          // After decay, only a very small bounciness remains
          particle.bounciness = particle.minBounciness;
        }

        // Only apply repulsion if the particle is close to the mouse
        const repelRadius = 100; // Define the radius for repulsion effect
        if (distance < repelRadius) {
          const forceDirectionX = distX / distance;
          const forceDirectionY = distY / distance;
          const force = (repelRadius - distance) / repelRadius; // The closer the particle, the stronger the force
          const directionX = forceDirectionX * force * 4;
          const directionY = forceDirectionY * force * 4;

          particle.vx += directionX;
          particle.vy += directionY;
        }

        // Lerp particles back to their original positions with bounciness
        const dx = particle.bx - particle.x;
        const dy = particle.by - particle.y;

        // Apply a stronger pull toward the target and allow overshooting based on remaining bounciness
        particle.vx += dx * 0.05 * particle.bounciness;
        particle.vy += dy * 0.05 * particle.bounciness;

        // Update particle position
        particle.x += particle.vx;
        particle.y += particle.vy;

        // Apply friction to gradually slow down the particles (damping effect)
        particle.vx *= particle.friction;
        particle.vy *= particle.friction;

        // Draw the particle
        ctx.beginPath();
        ctx.arc(particle.x, particle.y, particle.radius, 0, Math.PI * 2, false);
        ctx.fillStyle = 'white';
        ctx.fill();
        ctx.closePath();
      });

      requestAnimationFrame(render); // Repeat the render process
    };

    render(); // Start the animation

    return () => {
      // Cleanup
      window.removeEventListener('mousemove', handleMouseMove);
    };
  }, [mousePos, bouncinessDecay]);

  return <canvas ref={canvasRef} />;
};

export default ParticleText;
