import React from 'react';
import './Home.css';
import './Content.css';
import lockscreen from '../assets/Proximity-UI/lockscreen.mov';
import article from '../assets/Proximity-UI/article.mov';

const Home = () => {
    return (
        <div className="container content">
            <div className="row ">
                <div className="col-md-6 left-column">
                    <div className="fixed-content">

                        <video src={article} className="img-fluid w-100" controls loop muted>
                            Your browser does not support the video tag.
                        </video>
                        <video src={lockscreen} className="img-fluid w-100" controls loop muted>
                            Your browser does not support the video tag.
                        </video>


                        <div className="text-content">
                            <h2>Distance Based Design</h2>
                            <p>Research, Prototype</p>
                        </div>
                    </div>

                    <div className="scrolling-content">
                        {/* <img src="path-to-eiffel-tower-placeholder.jpg" alt="Eiffel Tower Bottom 2" className="bottom-image" /> */}
                    </div>
                </div>

                {/* Right Column */}
                <div className="col-md-6 right-column ">
                    <div className="content">
                        {/* Add your right column content here */}
                        <p>I like design that is changes, constantly in motion through interaction...</p>
                        <p>There are scenarios where you are near the display, yet not using it. Maybe you are walking around doing activies in the house, scrolling on your phone on your bed, or just trying to get a glimps without walking up close to your device. How can we portray this?</p>
                    </div>
                </div>
            </div>
        </div >
    );
};

export default Home;
