import React, { useEffect, useRef, useState } from 'react';
import './App.css';

const ParticleText = () => {
  const canvasRef = useRef(null);
  const [mousePos, setMousePos] = useState({ x: 0, y: 0 });

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    // Set canvas size
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;

    // Create an offscreen canvas for text rendering
    const bufferCanvas = document.createElement('canvas');
    const bufferCtx = bufferCanvas.getContext('2d');
    bufferCanvas.width = canvas.width;
    bufferCanvas.height = canvas.height;

    const particles = [];
    const numParticles = 5000; // Adjust the number of particles for better effect

    const text = "HCI Explorer of Digital-Physical Experiences";

    // Step 1: Draw the text on the offscreen buffer canvas
    bufferCtx.font = '50px Arial';
    bufferCtx.fillStyle = 'white';
    bufferCtx.textAlign = 'center';
    bufferCtx.fillText(text, canvas.width / 2, canvas.height / 2);

    // Step 2: Get pixel data of the text from the buffer canvas
    const imageData = bufferCtx.getImageData(0, 0, canvas.width, canvas.height);
    const data = imageData.data;

    // Step 3: Map particles to the text pixels
    for (let y = 0; y < canvas.height; y += 4) {
      for (let x = 0; x < canvas.width; x += 4) {
        const index = (y * canvas.width + x) * 4;
        if (data[index + 3] > 150) { // Check if the pixel is opaque
          particles.push({
            x: Math.random() * canvas.width, // Start with random positions
            y: Math.random() * canvas.height,
            bx: x, // Base x position (where the text is)
            by: y, // Base y position (where the text is)
            vx: 0,
            vy: 0,
            radius: 1.5,
          });
        }
      }
    }

    // Mousemove handler to update the mouse position
    const handleMouseMove = (e) => {
      setMousePos({ x: e.clientX, y: e.clientY });
    };

    window.addEventListener('mousemove', handleMouseMove);

    // Animation loop
    const render = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height); // Clear canvas

      particles.forEach(particle => {
        const distX = particle.x - mousePos.x;
        const distY = particle.y - mousePos.y;
        const distance = Math.sqrt(distX * distX + distY * distY);

        // Repel particles from mouse if they are close
        if (distance < 100) {
          const forceDirectionX = distX / distance;
          const forceDirectionY = distY / distance;
          const maxDistance = 100;
          const force = (maxDistance - distance) / maxDistance; // The closer the particle, the stronger the force
          const directionX = forceDirectionX * force * 4;
          const directionY = forceDirectionY * force * 4;

          particle.vx += directionX;
          particle.vy += directionY;
        }

        // Lerp particles back to their original positions
        particle.vx += (particle.bx - particle.x) * 0.05;
        particle.vy += (particle.by - particle.y) * 0.05;

        // Update particle position
        particle.x += particle.vx;
        particle.y += particle.vy;

        // Apply friction
        particle.vx *= 0.92;
        particle.vy *= 0.92;

        // Draw the particle
        ctx.beginPath();
        ctx.arc(particle.x, particle.y, particle.radius, 0, Math.PI * 2, false);
        ctx.fillStyle = 'white';
        ctx.fill();
        ctx.closePath();
      });

      requestAnimationFrame(render); // Repeat the render process
    };

    render(); // Start the animation

    return () => {
      // Cleanup
      window.removeEventListener('mousemove', handleMouseMove);
    };
  }, [mousePos]);

  return <canvas ref={canvasRef} />;
};

export default ParticleText;
