import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';
import CCapture from 'ccapture.js';
import './Home.css';

const App = () => {
    const containerRef = useRef(null);

    useEffect(() => {
        let container;
        let camera, scene, renderer;
        let uniforms;

        const loader = new THREE.TextureLoader();
        let texture, _500;

        loader.setCrossOrigin('anonymous');
        loader.load('https://s3-us-west-2.amazonaws.com/s.cdpn.io/982762/noise.png', (tex) => {
            texture = tex;
            texture.wrapS = THREE.RepeatWrapping;
            texture.wrapT = THREE.RepeatWrapping;
            texture.minFilter = THREE.LinearFilter;

            loader.load('https://i.ibb.co/bFzn3Yc/zegnio.png', (tex) => {
                _500 = tex;
                init();
                animate();
            });
        });

        const init = () => {
            container = containerRef.current;

            camera = new THREE.Camera();
            camera.position.z = 1;

            scene = new THREE.Scene();

            const geometry = new THREE.PlaneBufferGeometry(2, 2);

            uniforms = {
                u_time: { type: 'f', value: 1.0 },
                u_resolution: { type: 'v2', value: new THREE.Vector2() },
                u_pxaspect: { type: 'f', value: window.devicePixelRatio },
                u_noise: { type: 't', value: texture },
                u_text500: { type: 't', value: _500 },
                u_mouse: { type: 'v2', value: new THREE.Vector2(-0.1, -0.1) },
            };

            const material = new THREE.ShaderMaterial({
                uniforms: uniforms,
                vertexShader: document.getElementById('vertexShader').textContent,
                fragmentShader: document.getElementById('fragmentShader').textContent,
            });

            material.extensions.derivatives = true;

            const mesh = new THREE.Mesh(geometry, material);
            scene.add(mesh);

            renderer = new THREE.WebGLRenderer();
            renderer.setPixelRatio(window.devicePixelRatio);
            container.appendChild(renderer.domElement);

            onWindowResize();
            window.addEventListener('resize', onWindowResize, false);

            document.addEventListener('pointermove', handlePointerMove);
        };

        const handlePointerMove = (e) => {
            const ratio = window.innerHeight / window.innerWidth;
            uniforms.u_mouse.value.x = (e.pageX - window.innerWidth / 2) / window.innerWidth / ratio;
            uniforms.u_mouse.value.y = (e.pageY - window.innerHeight / 2) / window.innerHeight * -1;
        };

        const onWindowResize = () => {
            renderer.setSize(window.innerWidth, window.innerHeight);
            uniforms.u_resolution.value.x = renderer.domElement.width;
            uniforms.u_resolution.value.y = renderer.domElement.height;
        };

        const animate = () => {
            requestAnimationFrame(animate);
            render();
        };

        const render = () => {
            uniforms.u_time.value += 0.05;
            renderer.render(scene, camera);
        };

        return () => {
            window.removeEventListener('resize', onWindowResize);
            document.removeEventListener('pointermove', handlePointerMove);
        };
    }, []);

    return (
        <div id="container" ref={containerRef} className="wrap">
            {/* Shaders */}
            <script id="vertexShader" type="x-shader/x-vertex">
                {`void main() {
          gl_Position = vec4( position, 1.0 );
        }`}
            </script>
            <script id="fragmentShader" type="x-shader/x-fragment">
                {/* Fragment shader code here */}
            </script>
        </div>
    );
};

export default App;
