import React, { useEffect, useState } from 'react';
import io from 'socket.io-client';  // Import the Socket.IO client library
import Navbar from './components/Navbar';
import List from './components/List';
import Card from './components/Card';
import Weather from './components/Weather';
import './App.css';

function App() {

  const [isCompVisible, setIsCompVisible] = useState(true);
  const [isLowData, setIsLowData] = useState(false);
  useEffect(() => {
    const socket = io('http://localhost:4000');

    socket.on('data', function (data) {
      console.log('Data received:', data);

      if (parseInt(data) < 63) {
        setIsCompVisible(false); // Hide navbar if data is under 50
        setIsLowData(true);
      } else {
        setIsCompVisible(true); // Show navbar if data is 50 or more
        setIsLowData(false);
      }

      // document.getElementById('sample').style.opacity = data + "%";
    });


    // Cleanup when the component unmounts
    return () => {
      socket.disconnect();
    };
  }, []);

  const topStories = [
    {
      title: "React 18 Released: What's New?",
      image: "https://ipsf.net/wp-content/uploads/2021/12/dummy-image-square.webp",
      link: "#"
    },
    {
      title: "JavaScript's New Features in 2024",
      image: "https://ipsf.net/wp-content/uploads/2021/12/dummy-image-square.webp",
      link: "#"
    },
    {
      title: "Web Development Trends to Watch",
      image: "https://ipsf.net/wp-content/uploads/2021/12/dummy-image-square.webp",
      link: "#"
    }
  ];



  let displayedStories = topStories;
  if (isLowData) {
    displayedStories = topStories.slice(0, 2);
  }
  const weatherForecast = [
    {
      title: "10:00 AM - temperature: 22",
      image: "https://via.placeholder.com/50",
      link: "#"
    },
    {
      title: "11:00 AM - temperature: 22",
      image: "https://via.placeholder.com/50",
      link: "#"
    },
    {
      title: "12:00 AM - temperature: 22",
      image: "https://via.placeholder.com/50",
      link: "#"
    },
    {
      title: "1:00 AM - temperature: 22",
      image: "https://via.placeholder.com/50",
      link: "#"
    },
    {
      title: "2:00 PM - temperature: 22",
      image: "https://via.placeholder.com/50",
      link: "#"
    }
  ];
  // const weatherForecast = [
  //   { time: '10:00 AM', icon: 'https://via.placeholder.com/50', temperature: 22 },
  //   { time: '11:00 AM', icon: 'https://via.placeholder.com/50', temperature: 24 },
  //   { time: '12:00 PM', icon: 'https://via.placeholder.com/50', temperature: 25 },
  //   { time: '1:00 PM', icon: 'https://via.placeholder.com/50', temperature: 26 },
  // ];

  return (
    <div className={`App ${isLowData ? 'low-data' : ''}`}> {/* Apply conditional class */}
      {isCompVisible && <Navbar />}
      <div className="container">
        <div className="content">
          <div id="sample"></div>
          <div className="row">
            <div className="col-md-8">
              <div className="text-container">
                {isLowData && (
                  <img
                    src="https://pics.freeartbackgrounds.com/fullhd/Spring_Maple_Tree_Branch_and_Sky_Background-257.jpg"
                    alt="Transport"
                    className="background-image"
                  />
                )}
                <h1>This kind of public transportation is growing</h1>
                <h2>Also: How Tim Hortons should fill your reusable cup</h2>
                {!isLowData && (
                  <p>CBC News · Posted: Jul 11, 2024 12:40 PM EDT | Last Updated: July 12</p>
                )}
                {!isLowData && (
                  <img src="https://pics.freeartbackgrounds.com/fullhd/Spring_Maple_Tree_Branch_and_Sky_Background-257.jpg" alt="Transport" className="regImg" />
                )}
                {!isLowData && (
                  <p>Fewer Canadians have been using public buses and trains since the pandemic. But one new type of public transportation is seeing huge growth in cities across Canada — bike sharing...</p>
                )}
              </div>
            </div>
            <div className="col-md-4 list-container">
              <h3>Top Stories of the Day</h3>
              {!isLowData && (
                <List articles={displayedStories} />
              )}
              {isLowData && (
                <List articles={weatherForecast} />
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
