import React, { useEffect } from 'react';
import * as THREE from 'three';
import { FontLoader } from 'three/examples/jsm/loaders/FontLoader.js';
import { Container, Row, Col } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import './Home.css'; // Ensure you have this CSS file for custom styles
import image1 from '../assets/img1.png';
import image2 from '../assets/img2.png';
import image3 from '../assets/img3.png';
import image4 from '../assets/img4.png';
import image5 from '../assets/img5.png';
import image6 from '../assets/img6.png';

const Home = () => {

    // Three.js and particle text effect logic
    useEffect(() => {
        const preload = () => {
            let manager = new THREE.LoadingManager();
            manager.onLoad = function () {
                if (typo) {
                    const environment = new Environment(typo, particle);
                }
            };

            let typo = null;
            const loader = new FontLoader(manager);
            loader.load(
                'https://res.cloudinary.com/dydre7amr/raw/upload/v1612950355/font_zsd4dr.json',
                function (font) {
                    typo = font;
                }
            );
            const particle = new THREE.TextureLoader(manager).load(
                'https://res.cloudinary.com/dfvtkoboz/image/upload/v1605013866/particle_a64uzf.png'
            );
        };

        if (
            document.readyState === 'complete' ||
            (document.readyState !== 'loading' && !document.documentElement.doScroll)
        )
            preload();
        else document.addEventListener('DOMContentLoaded', preload);

        class Environment {
            constructor(font, particle) {
                this.font = font;
                this.particle = particle;
                this.container = document.querySelector('#magic');
                this.scene = new THREE.Scene();
                this.createCamera();
                this.createRenderer();
                this.setup();
                this.bindEvents();
            }

            bindEvents() {
                window.addEventListener('resize', this.onWindowResize.bind(this));
            }

            setup() {
                // For debugging: add a simple cube to verify WebGL setup
                const geometry = new THREE.BoxGeometry(1, 1, 1);
                const material = new THREE.MeshBasicMaterial({ color: 0x00ff00 });
                const cube = new THREE.Mesh(geometry, material);
                this.scene.add(cube); // See if this renders

                // Commenting this out for debugging first
                // this.createParticles = new CreateParticles(this.scene, this.font, this.particle, this.camera, this.renderer);
            }

            render() {
                this.renderer.render(this.scene, this.camera);
            }

            createCamera() {
                this.camera = new THREE.PerspectiveCamera(
                    65,
                    this.container.clientWidth / this.container.clientHeight,
                    1,
                    10000
                );
                this.camera.position.set(0, 0, 50); // Moved closer for visibility
            }

            createRenderer() {
                this.renderer = new THREE.WebGLRenderer();
                this.renderer.setSize(this.container.clientWidth, this.container.clientHeight);
                this.renderer.setPixelRatio(window.devicePixelRatio); // Updated for proper ratio
                this.container.appendChild(this.renderer.domElement);
                this.renderer.setAnimationLoop(() => {
                    this.render();
                });
            }

            onWindowResize() {
                this.camera.aspect = this.container.clientWidth / this.container.clientHeight;
                this.camera.updateProjectionMatrix();
                this.renderer.setSize(this.container.clientWidth, this.container.clientHeight);
            }
        }

        // Vertex and Fragment shader scripts (ensure these are loaded correctly)
        const vertexShaderScript = document.createElement('script');
        vertexShaderScript.type = 'x-shader/x-vertex';
        vertexShaderScript.id = 'vertexshader';
        vertexShaderScript.text = `
          attribute float size;
          attribute vec3 customColor;
          varying vec3 vColor;
          void main() {
            vColor = customColor;
            vec4 mvPosition = modelViewMatrix * vec4(position, 1.0);
            gl_PointSize = size * (300.0 / -mvPosition.z);
            gl_Position = projectionMatrix * mvPosition;
          }
        `;
        document.body.appendChild(vertexShaderScript);

        const fragmentShaderScript = document.createElement('script');
        fragmentShaderScript.type = 'x-shader/x-fragment';
        fragmentShaderScript.id = 'fragmentshader';
        fragmentShaderScript.text = `
          uniform vec3 color;
          uniform sampler2D pointTexture;
          varying vec3 vColor;
          void main() {
            gl_FragColor = vec4(color * vColor, 1.0);
            gl_FragColor = gl_FragColor * texture2D(pointTexture, gl_PointCoord);
          }
        `;
        document.body.appendChild(fragmentShaderScript);
    }, []);


    return (
        <div className="background">
            <div id="magic" />
            <div className="playground">
                <div className="bottomPosition">
                    <svg className="logo" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 278.8 278.8">
                        <circle fill="#000205" cx="139.4" cy="139.4" r="139.4" />
                        <g fill="none" stroke="#FFF" strokeWidth="6.062" strokeMiterlimit="10">
                            <path d="M214 183.4l-74.6 43.1-75.5-43.6V95.8l75.5-43.5 75.4 43.5v59.8l-11.3 6.5z" />
                            <path d="M139.4 226.5l-18.8-38.2 18.8 10.8 75.4-43.5M63.9 95.8l75.3 43.6 75.6-43.6M139.2 139.4v59.7" />
                        </g>
                    </svg>
                    <h1 className="special">
                        RICARDO SANPRIETO <br />
                        <span className="minText">Creative Frontend Developer</span>
                    </h1>
                </div>
            </div>

            Overlayed Bootstrap Container for Images and Titles
            <div className="overlay-content">
                <Container>
                    <Row className="mt-5">
                        <Col md={4} className="text-center">
                            <Link to="/project1">
                                <img src={image1} alt="Project 1" className="img-fluid w-100" />
                                <h5>Project 1 Title</h5>
                            </Link>
                        </Col>
                        <Col md={4} className="text-center">
                            <Link to="/project2">
                                <img src={image2} alt="Project 2" className="img-fluid w-100" />
                                <h5>Project 2 Title</h5>
                            </Link>
                        </Col>
                        <Col md={4} className="text-center">
                            <Link to="/project3">
                                <img src={image3} alt="Project 3" className="img-fluid w-100" />
                                <h5>Project 3 Title</h5>
                            </Link>
                        </Col>
                    </Row>
                    <Row className="mt-4">
                        <Col md={4} className="text-center">
                            <Link to="/project4">
                                <img src={image4} alt="Project 4" className="img-fluid w-100" />
                                <h5>Project 4 Title</h5>
                            </Link>
                        </Col>
                        <Col md={4} className="text-center">
                            <Link to="/project5">
                                <img src={image5} alt="Project 5" className="img-fluid w-100" />
                                <h5>Project 5 Title</h5>
                            </Link>
                        </Col>
                        <Col md={4} className="text-center">
                            <Link to="/project6">
                                <img src={image6} alt="Project 6" className="img-fluid w-100" />
                                <h5>Project 6 Title</h5>
                            </Link>
                        </Col>
                    </Row>
                </Container>
            </div>
        </div>
    );
};

export default Home;
